import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import Comment from "./Comment"; // Import your Comment module

export const Explore = () => {
  const location = useLocation();
  const book = location.state?.book;
  const [comments, setComments] = useState([]);
  const [newCommentText, setNewCommentText] = useState("");

  // Function to fetch comments for a specific book
  const fetchComments = async (bookId) => {
    try {
      const data = await Comment.getCommentsByBookId(bookId);
      setComments(data);
    } catch (error) {
      console.error("Error fetching comments:", error.message);
    }
  };

  useEffect(() => {
    if (book) {
      fetchComments(book._id); // Fetch comments when book details are available
    }
  }, [book]);

  // Function to handle adding a new comment
  const handleAddComment = async () => {
    if (newCommentText.trim() !== "") {
      try {
        const newCommentData = {
          commenter: "User", // You may replace this with actual user data
          book: book._id,
          text: newCommentText,
        };

        await Comment.addComment(newCommentData);
        fetchComments(book._id); // Fetch comments after adding a new one
        setNewCommentText(""); // Clear the comment input field after adding
      } catch (error) {
        console.error("Error adding comment:", error.message);
      }
    }
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        padding: "20px",
        boxShadow: "0px 0px 5px #ccc",
        marginLeft: "20px",
        marginTop: "20px",
        cursor: "pointer",
      }}
    >
      <img
        src={`http://localhost:8000/${book.image}`}
        alt={book.name}
        style={{
          height: "250px",
          width: "250px",
          objectFit: "contain",
        }}
      />

      <br />
      <div>
        <strong>:</strong> <small>{book.name}</small>
      </div>
      <div>
        <strong>Author:</strong> <small>{book.author}</small>
      </div>
      <div>
        <strong>Genre:</strong> <small>{book.genre}</small>
      </div>
      <div>
        <strong>Publisher:</strong> <small>{book.publisher}</small>
      </div>
      <div>
        <strong>Description:</strong> <small>{book.description}</small>
      </div>
      <div>
        <strong>price:</strong> <small>{book.price}</small>
      </div>
      {book.pdf && (
        <div>
          <a
            href={`http://localhost:8000/${book.pdf}`}
            target="_blank"
            rel="noopener noreferrer"
          >
            View PDF
          </a>
        </div>
      )}
      <div>
        <h3>Comments</h3>
        <textarea
          value={newCommentText}
          onChange={(e) => setNewCommentText(e.target.value)}
          placeholder="Add a new comment..."
        ></textarea>
        <button onClick={handleAddComment}>Add Comment</button>
        <div>
          {comments.map((comment) => (
            <div key={comment._id}>
              <p>{comment.text}</p>
              <button
                onClick={() => handleUpdateComment(comment._id, "New Text")}
              >
                Update
              </button>
              <button onClick={() => handleDeleteComment(comment._id)}>
                Delete
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};
