import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

const UpdateBook = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [book, setBook] = useState({
    name: "",
    author: "",
    genre: "",
    description: "",
    publisher: "",
    price: 0,
    image: null,
    pdf: null,
  });

  useEffect(() => {
    async function fetchBook() {
      try {
        const response = await axios.get(`http://localhost:8000/books/${id}`);
        setBook(response.data);
      } catch (error) {
        console.error("Error fetching book:", error);
      }
    }
    fetchBook();
  }, [id]);

  const handleChange = (e) => {
    const { name, value, files } = e.target;
    setBook((prevBook) => ({
      ...prevBook,
      [name]: files ? files[0] : value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("name", book.name);
    formData.append("author", book.author);
    formData.append("genre", book.genre);
    formData.append("description", book.description);
    formData.append("publisher", book.publisher);
    formData.append("price", book.price);
    formData.append("image", book.image);
    formData.append("pdf", book.pdf);

    try {
      const response = await axios.put(
        `http://localhost:8000/books/${id}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      console.log("Book updated:", response.data);
      navigate("/"); // Redirect to the home page after updating the book
    } catch (error) {
      console.error("Error updating book:", error);
    }
  };

  return (
    <div className="input-container">
      <h2>Update Book</h2>
      <form
        style={{
          display: "flex",
          flexDirection: "column",
        }}
        onSubmit={handleSubmit}
      >
        Name:
        <input
          type="text"
          name="name"
          value={book.name}
          onChange={handleChange}
        />
        Author:
        <input
          type="text"
          name="author"
          value={book.author}
          onChange={handleChange}
        />
        Genre:
        <input
          type="text"
          name="genre"
          value={book.genre}
          onChange={handleChange}
        />
        Publisher:
        <input
          type="text"
          name="publisher"
          value={book.publisher}
          onChange={handleChange}
        />
        Description:
        <textarea
          name="description"
          rows="4"
          value={book.description}
          onChange={handleChange}
        ></textarea>
        Price:
        <input
          type="number"
          name="price"
          value={book.price}
          onChange={handleChange}
        />
        Image:
        <input
          type="file"
          name="image"
          accept="image/*"
          onChange={handleChange}
        />
        PDF:
        <input type="file" name="pdf" accept=".pdf" onChange={handleChange} />
        <button type="submit">Update Book</button>
      </form>
    </div>
  );
};

export default UpdateBook;
