import React, { useRef, useState, useEffect } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useNavigate, useParams } from "react-router-dom";
import CustomFileInput from "./CustomFileInput";
import "../../App.css";

const UpdateBook = () => {
  const navigate = useNavigate();
  const { id } = useParams();

  const [book, setBook] = useState({
    name: "",
    author: "",
    genre: "",
    publisher: "",
    description: "",
    price: 0,
    image: null, // Modified to initialize as null
    pdf: null, // Modified to initialize as null
  });

  useEffect(() => {
    async function fetchBook() {
      try {
        const response = await axios.get(`http://localhost:8000/books/${id}`);
        setBook(response.data);
      } catch (error) {
        console.error("Error fetching book:", error);
      }
    }
    fetchBook();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append("name", book.name);
      formData.append("author", book.author);
      formData.append("genre", book.genre);
      formData.append("publisher", book.publisher);
      formData.append("description", book.description);
      formData.append("price", book.price);

      if (book.image) {
        formData.append("image", book.image);
      }
      if (book.pdf) {
        formData.append("pdf", book.pdf);
      }

      const response = await axios.put(
        `http://localhost:8000/books/${id}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (response.status === 200) {
        toast.success("Book updated successfully");
        navigate("/"); // Redirect to the home page after updating the book
      } else {
        toast.error("Failed to update book");
      }
    } catch (err) {
      console.error("Error updating book:", err);
      toast.error(`Failed to update book. Error: ${err.message}`);
    }
  };

  // Handlers for updating the image and pdf in the book state
  const handleImageChange = (file) => {
    setBook({ ...book, image: file });
  };

  const handlePDFChange = (file) => {
    setBook({ ...book, pdf: file });
  };
  return (
    <div className="input-container">
      <ToastContainer />
      <form
        style={{
          display: "flex",
          flexDirection: "column",
        }}
        onSubmit={handleSubmit}
      >
        Name:
        <input
          type="text"
          id="name"
          name="name"
          value={book.name}
          onChange={handleInputChange}
        />
        Author:
        <input
          type="text"
          id="author"
          name="author"
          value={book.author}
          onChange={handleInputChange}
        />
        Genre:
        <input
          type="text"
          id="genre"
          name="genre"
          value={book.genre}
          onChange={handleInputChange}
        />
        Publisher:
        <input
          type="text"
          id="publisher"
          name="publisher"
          value={book.publisher}
          onChange={handleInputChange}
        />
        Description:
        <textarea
          name="description"
          rows="4"
          value={book.description}
          onChange={handleInputChange}
        ></textarea>
        Price:
        <input
          type="number"
          id="price"
          name="price"
          value={book.price}
          onChange={handleInputChange}
        />
        <CustomFileInput
          accept="image/*"
          label="Image"
          clearButtonLabel="Clear Image"
          onChange={handleImageChange}
          initialFileName={book.image ? book.image.name : ""}
        />
        {/* CustomFileInput for PDF */}
        <CustomFileInput
          accept=".pdf"
          label="PDF"
          clearButtonLabel="Clear PDF"
          onChange={handlePDFChange}
          initialFileName={book.pdf ? book.pdf.name : ""}
        />
        {/* Submit button */}
        <button type="submit">Update Book</button>
      </form>
    </div>
  );
};

export default UpdateBook;
