import React, { useState, useEffect } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useNavigate, useParams } from "react-router-dom";
import "../../App.css";

const UpdateBook = () => {
  const navigate = useNavigate();
  const { id } = useParams();

  const [book, setBook] = useState({
    name: "",
    author: "",
    genre: "",
    publisher: "",
    description: "",
    price: 0,
    image: null,
    pdf: null,
  });

  useEffect(() => {
    async function fetchBook() {
      try {
        const response = await axios.get(`http://localhost:8000/books/${id}`);
        setBook(response.data);
      } catch (error) {
        console.error("Error fetching book:", error);
      }
    }
    fetchBook();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validation: Check if required fields are not empty
    if (
      !book.name.trim() ||
      !book.author.trim() ||
      !book.genre.trim() ||
      !book.publisher.trim() ||
      !book.description.trim() ||
      !book.price
    ) {
      toast.error("Please fill in all fields");
      return;
    }

    const formData = new FormData();
    formData.append("name", book.name);
    formData.append("author", book.author);
    formData.append("genre", book.genre);
    formData.append("publisher", book.publisher);
    formData.append("description", book.description);
    formData.append("price", book.price);
    if (!book.image) {
      formData.append("image", ""); // Retaining existing image
    } else {
      formData.append("image", book.image);
    }

    if (!book.pdf) {
      formData.append("pdf", ""); // Retaining existing PDF
    } else {
      formData.append("pdf", book.pdf);
    }
    try {
      const response = await axios.put(
        `http://localhost:8000/books/${id}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (response.status === 200) {
        toast.success("Book updated successfully");
        navigate("/"); // Redirect to the home page after updating the book
      } else {
        toast.error("Failed to update book");
      }
    } catch (err) {
      console.error("Error updating book:", err);
      toast.error(`Failed to update book. Error: ${err.message}`);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBook({ ...book, [name]: value });
  };

  return (
    <div className="input-container">
      <ToastContainer />
      <form
        style={{
          display: "flex",
          flexDirection: "column",
        }}
        onSubmit={handleSubmit}
      >
        Name:
        <input
          type="text"
          id="name"
          name="name"
          value={book.name}
          onChange={handleInputChange}
        />
        Author:
        <input
          type="text"
          id="author"
          name="author"
          value={book.author}
          onChange={handleInputChange}
        />
        Genre:
        <input
          type="text"
          id="genre"
          name="genre"
          value={book.genre}
          onChange={handleInputChange}
        />
        Publisher:
        <input
          type="text"
          id="publisher"
          name="publisher"
          value={book.publisher}
          onChange={handleInputChange}
        />
        Description:
        <textarea
          name="description"
          rows="4"
          value={book.description}
          onChange={handleInputChange}
        ></textarea>
        Price:
        <input
          type="number"
          id="price"
          name="price"
          value={book.price}
          onChange={handleInputChange}
        />
        {book.image && (
          <img
            src={`http://localhost:8000/${book.image}`}
            alt="Book Cover"
            style={{ maxWidth: "200px" }}
          />
        )}
        {book.pdf && (
          <a
            src={`http://localhost:8000/${book.pdf}`}
            target="_blank"
            rel="noopener noreferrer"
          >
            View PDF
          </a>
        )}
        {/* File inputs for updating image and PDF */}
        <input type="file" id="image" name="image" accept="image/*" />
        <input type="file" id="pdf" name="pdf" accept=".pdf" />
        {/* Submit button */}
        <button type="submit">Update Book</button>
      </form>
    </div>
  );
};

export default UpdateBook;
