import React, { useState, useEffect } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useParams, useNavigate } from "react-router-dom";
import "../../App.css";

const UpdateBook = () => {
  const navigate = useNavigate();
  const { id } = useParams(); // Assuming your route has an 'id' parameter for the book

  const [bookData, setBookData] = useState({
    name: "",
    author: "",
    genre: "",
    publisher: "",
    description: "",
    price: 0,
    image: null,
    pdf: null,
  });

  useEffect(() => {
    const fetchBookData = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/books/${id}`);
        if (response.status === 200) {
          const { name, author, genre, publisher, description, price } =
            response.data;
          setBookData({
            name,
            author,
            genre,
            publisher,
            description,
            price,
            image: null, // Initialize these as null or empty
            pdf: null, // They will be updated when changed
          });
        }
      } catch (error) {
        console.error("Error fetching book data:", error);
        // Handle error here (e.g., show error message)
      }
    };

    fetchBookData();
  }, [id]);

  const handleFileChange = (e) => {
    setBookData({
      ...bookData,
      [e.target.name]: e.target.files[0],
    });
  };

  const handleChange = (e) => {
    setBookData({
      ...bookData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("name", bookData.name);
    formData.append("author", bookData.author);
    formData.append("genre", bookData.genre);
    formData.append("description", bookData.description);
    formData.append("publisher", bookData.publisher);
    formData.append("price", bookData.price);
    formData.append("image", bookData.image);
    formData.append("pdf", bookData.pdf);

    try {
      const response = await axios.put(
        `http://localhost:8000/books/${id}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      if (response.status === 200) {
        toast.success("Updated Book");
        navigate("/"); // Redirect to the home page after updating the book
      } else {
        toast.error("Failed to update book. Response:", response.data);
      }
    } catch (err) {
      toast.error("Failed to update book. Error:", err.message);
    }
  };

  return (
    <div className="input-container">
      <ToastContainer />
      <form
        style={{
          display: "flex",
          flexDirection: "column",
        }}
        onSubmit={handleSubmit}
      >
        Name:
        <input
          type="text"
          id="name"
          name="name"
          value={bookData.name}
          onChange={handleChange}
        />
        Author:
        <input
          type="text"
          id="author"
          name="author"
          value={bookData.author}
          onChange={handleChange}
        />
        {/* Other input fields (similar to above) */}
        Image:
        <input
          type="file"
          id="image"
          name="image"
          accept="image/*"
          onChange={handleFileChange}
        />
        PDF:
        <input
          type="file"
          id="pdf"
          name="pdf"
          accept=".pdf"
          onChange={handleFileChange}
        />
        <button type="submit">Update Book</button>
      </form>
    </div>
  );
};

export default UpdateBook;
