import React, { useState, useEffect } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useNavigate, useParams } from "react-router-dom";
import "../../App.css";

const UpdateBook = () => {
  const navigate = useNavigate();
  const { id } = useParams();
  const [bookData, setBookData] = useState({
    name: "",
    author: "",
    genre: "",
    publisher: "",
    description: "",
    price: "",
    image: null,
    pdf: null,
  });

  useEffect(() => {
    fetchBookData();
  }, []);

  const fetchBookData = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/books/${id}`);
      const { name, author, genre, publisher, description, price, image, pdf } =
        response.data;
      setBookData({
        name,
        author,
        genre,
        publisher,
        description,
        price,
        image,
        pdf,
      });
    } catch (error) {
      toast.error("Failed to fetch book data. Error:", error.message);
      navigate("/"); // Redirect to home page on error
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);

    try {
      const response = await axios.put(
        `http://localhost:8000/books/${id}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      if (response.status === 200) {
        toast.success("Updated Book");
        navigate("/"); // Redirect to home page after successful update
      } else {
        toast.error("Failed to update book. Response:", response.data);
      }
    } catch (err) {
      toast.error("Failed to update book. Error:", err.message);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setBookData({ ...bookData, [name]: value });
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    setBookData({ ...bookData, [name]: files[0] });
  };

  return (
    <div className="input-container">
      <ToastContainer />
      <form
        style={{
          display: "flex",
          flexDirection: "column",
        }}
        onSubmit={handleSubmit}
      >
        Name:
        <input
          type="text"
          id="name"
          name="name"
          value={bookData.name}
          onChange={handleChange}
        />
        Author:
        <input
          type="text"
          id="author"
          name="author"
          value={bookData.author}
          onChange={handleChange}
        />
        Genre:
        <input
          type="text"
          id="genre"
          name="genre"
          value={bookData.genre}
          onChange={handleChange}
        />
        Publisher:
        <input
          type="text"
          id="publisher"
          name="publisher"
          value={bookData.publisher}
          onChange={handleChange}
        />
        Description:
        <textarea
          name="description"
          rows="4"
          value={bookData.description}
          onChange={handleChange}
        ></textarea>
        Price:
        <input
          type="number"
          id="price"
          name="price"
          value={bookData.price}
          onChange={handleChange}
        />
        Image:
        <input
          type="file"
          id="image"
          name="image"
          accept="image/*"
          onChange={handleFileChange}
        />
        PDF:
        <input
          type="file"
          id="pdf"
          name="pdf"
          accept=".pdf"
          onChange={handleFileChange}
        />
        <button type="submit">Update Book</button>
      </form>
    </div>
  );
};

export default UpdateBook;
