import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "../../App.css";

const Login = ({ setIsLoggedIn }) => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });

  const navigate = useNavigate();
  const [formSubmitted, setFormSubmitted] = useState(false); // State to track form submission

  useEffect(() => {
    const isLoggedIn = localStorage.getItem("isLoggedIn");
    if (isLoggedIn === "true") {
      navigate("/");
    }
  }, [navigate]);

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setFormSubmitted(true); // Set formSubmitted to true on form submission

    // Check if email or password fields are empty
    if (!formData.email || !formData.password) {
      alert("Please enter both email and password."); // For demonstration purposes; replace with proper error handling
      return; // Exit function early if fields are empty
    }

    try {
      const response = await fetch("http://localhost:8000/users/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        localStorage.setItem("isLoggedIn", "true");
        setIsLoggedIn(true);
        navigate("/"); // Redirect to home page after successful login
      } else {
        const errorData = await response.json();
        if (errorData.type === "email") {
          alert("Email not found. Please check your email."); // For demonstration purposes; replace with proper error handling
        } else if (errorData.type === "password") {
          alert("Incorrect password. Please try again."); // For demonstration purposes; replace with proper error handling
        } else {
          alert("An error occurred. Please try again."); // For demonstration purposes; replace with proper error handling
        }
      }
    } catch (error) {
      console.error("Error:", error);
      alert("An error occurred. Please try again."); // For demonstration purposes; replace with proper error handling
    }
  };

  return (
    <div className="login-container">
      <h1>User Login</h1>
      <form onSubmit={handleLogin}>
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={formData.email}
          onChange={handleInputChange}
          required
        />
        <br />
        <br />
        <label htmlFor="password">Password:</label>
        <input
          type="password"
          id="password"
          name="password"
          value={formData.password}
          onChange={handleInputChange}
          required
        />
        <br />
        <br />
        {formSubmitted && (!formData.email || !formData.password) && (
          <p style={{ color: "red" }}>Please enter both email and password.</p>
        )}

        <input type="submit" value="Login" />
      </form>
    </div>
  );
};

export default Login;
