import React, { useState, useEffect } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useNavigate, useParams } from "react-router-dom";
import CustomFileInput from "./CustomFileInput";
import "../../App.css";

const UpdateBook = () => {
  const navigate = useNavigate();
  const { id } = useParams();

  const [book, setBook] = useState({
    name: "",
    author: "",
    genre: "",
    publisher: "",
    description: "",
    price: 0,
    image: "",
    pdf: "",
  });

  useEffect(() => {
    async function fetchBook() {
      try {
        const response = await axios.get(`http://localhost:8000/books/${id}`);
        const {
          name,
          author,
          genre,
          publisher,
          description,
          price,
          image,
          pdf,
        } = response.data;
        setBook({
          name,
          author,
          genre,
          publisher,
          description,
          price,
          image,
          pdf,
        });
      } catch (error) {
        console.error("Error fetching book:", error);
      }
    }
    fetchBook();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.put(
        `http://localhost:8000/books/${id}`,
        book // Assuming your backend API expects the updated book object
      );

      if (response.status === 200) {
        toast.success("Book updated successfully");
        navigate("/"); // Redirect to the home page after updating the book
      } else {
        toast.error("Failed to update book");
      }
    } catch (err) {
      console.error("Error updating book:", err);
      toast.error(`Failed to update book. Error: ${err.message}`);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBook({ ...book, [name]: value });
  };
  return (
    <div className="input-container">
      <ToastContainer />
      <form
        style={{
          display: "flex",
          flexDirection: "column",
        }}
        onSubmit={handleSubmit}
      >
        Name:
        <input
          type="text"
          id="name"
          name="name"
          value={book.name}
          onChange={handleInputChange}
        />
        Author:
        <input
          type="text"
          id="author"
          name="author"
          value={book.author}
          onChange={handleInputChange}
        />
        Genre:
        <input
          type="text"
          id="genre"
          name="genre"
          value={book.genre}
          onChange={handleInputChange}
        />
        Publisher:
        <input
          type="text"
          id="publisher"
          name="publisher"
          value={book.publisher}
          onChange={handleInputChange}
        />
        Description:
        <textarea
          name="description"
          rows="4"
          value={book.description}
          onChange={handleInputChange}
        ></textarea>
        Price:
        <input
          type="number"
          id="price"
          name="price"
          value={book.price}
          onChange={handleInputChange}
        />
        <CustomFileInput
          accept="image/*"
          label="Image"
          clearButtonLabel="Clear Image"
          onChange={(e) => setBook({ ...book, image: e.target.files[0] })}
          initialFileName={book.image ? book.image.name : ""}
        />
        {/* CustomFileInput for PDF */}
        <CustomFileInput
          accept=".pdf"
          label="PDF"
          clearButtonLabel="Clear PDF"
          onChange={(e) => setBook({ ...book, pdf: e.target.files[0] })}
          initialFileName={book.pdf ? book.pdf.name : ""}
        />
        {/* Submit button */}
        <button type="submit">Update Book</button>
      </form>
    </div>
  );
};

export default UpdateBook;
