import React, { useState, useEffect } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useParams, useNavigate } from "react-router-dom";
import "../../App.css";

const UpdateBook = () => {
  const navigate = useNavigate();
  const { id } = useParams(); // Assuming your route has an 'id' parameter for the book

  const [bookData, setBookData] = useState({
    name: "",
    author: "",
    genre: "",
    publisher: "",
    description: "",
    price: 0,
    image: null,
    pdf: null,
  });

  useEffect(() => {
    // Fetch book data using the ID parameter
    const fetchBookData = async () => {
      try {
        const response = await axios.get(`http://localhost:8000/books/${id}`);
        if (response.status === 200) {
          setBookData(response.data); // Set book data to state
        }
      } catch (error) {
        console.error("Error fetching book data:", error);
        // Handle error here (e.g., show error message)
      }
    };

    fetchBookData();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);

    try {
      const response = await axios.put(
        `http://localhost:8000/books/update/${id}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      if (response.status === 200) {
        toast.success("Updated Book");
        navigate("/"); // Redirect to the home page after updating the book
      } else {
        toast.error("Failed to update book. Response:", response.data);
      }
    } catch (err) {
      toast.error("Failed to update book. Error:", err.message);
    }
  };

  const handleChange = (e) => {
    setBookData({
      ...bookData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className="input-container">
      <ToastContainer />
      <form
        style={{
          display: "flex",
          flexDirection: "column",
        }}
        onSubmit={handleSubmit}
      >
        Name:
        <input
          type="text"
          id="name"
          name="name"
          value={bookData.name}
          onChange={handleChange}
        />
        Author:
        <input
          type="text"
          id="author"
          name="author"
          value={bookData.author}
          onChange={handleChange}
        />
        Genre:
        <input
          type="text"
          name="genre"
          value={book.genre}
          onChange={handleChange}
        />
        Publisher:
        <input
          type="text"
          name="publisher"
          value={book.publisher}
          onChange={handleChange}
        />
        Description:
        <textarea
          name="description"
          rows="4"
          value={book.description}
          onChange={handleChange}
        ></textarea>
        Price:
        <input
          type="number"
          name="price"
          value={book.price}
          onChange={handleChange}
        />
        Image:
        <input
          type="file"
          name="image"
          accept="image/*"
          onChange={handleChange}
        />
        PDF:
        <input type="file" name="pdf" accept=".pdf" onChange={handleChange} />
        <button type="submit">Update Book</button>
      </form>
    </div>
  );
};

export default UpdateBook;
