import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { Document, Page, pdfjs } from "@react-pdf/renderer";

export const Explore = () => {
  const location = useLocation();
  const book = location.state?.book; // Ensure the book object exists
  const [showPDF, setShowPDF] = useState(false); // State to toggle PDF display
  useEffect(() => {
    // Set PDF.js worker source when the component mounts
    pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;
  }, []);

  // Conditional rendering if book details are available
  if (!book) {
    return <div>Loading...</div>; // Display a loading indicator or handle the case when book details are not available
  }

  const togglePDFView = () => {
    setShowPDF(!showPDF); // Toggle PDF display when the button is clicked
  };

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        padding: "20px",
        boxShadow: "0px 0px 5px #ccc",
        marginLeft: "20px",
        marginTop: "20px",
        cursor: "pointer",
      }}
    >
      <img
        src={`http://localhost:8000/${book.image}`}
        alt={book.name}
        style={{
          height: "250px",
          width: "250px",
          objectFit: "contain",
        }}
      />

      <br />
      <div>
        <strong>Name:</strong> <small>{book.name}</small>
      </div>
      <div>
        <strong>Author:</strong> <small>{book.author}</small>
      </div>
      <div>
        <strong>Genre:</strong> <small>{book.genre}</small>
      </div>
      <div>
        <strong>Publisher:</strong> <small>{book.publisher}</small>
      </div>
      <div>
        <strong>Description:</strong> <small>{book.description}</small>
      </div>
      <div>
        <strong>Price:</strong> <small>{book.price}</small>
      </div>
      {book.pdf && !showPDF && (
        <div>
          <button onClick={togglePDFView}>View PDF</button>
        </div>
      )}
      {showPDF && (
        <div>
          <button onClick={togglePDFView}>Close PDF</button>
          <Document file={`http://localhost:8000/${book.pdf}`}>
            <Page pageNumber={1} width={600} />
          </Document>
        </div>
      )}
    </div>
  );
};

export default Explore;
