import React, { useState, useEffect } from "react";
import axios from "axios";

const HomePage = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    // Fetch book details from the server when the component mounts
    const fetchBooks = async () => {
      try {
        const response = await axios.get("http://localhost:8000/books");
        if (response.data) {
          // Set the retrieved book details to the state
          setBooks(response.data);
        }
      } catch (error) {
        console.error("Error fetching books:", error);
      }
    };

    fetchBooks();
  }, []); // Empty dependency array to fetch books only once when the component mounts

  return (
    <div>
      <h1>Books</h1>
      {books.length === 0 ? (
        <p>No books available</p>
      ) : (
        <div>
          {books.map((book) => (
            <div key={book._id}>
              <h2>{book.name}</h2>
              <p>Author: {book.author}</p>
              <p>Genre: {book.genre}</p>
              <p>Publisher: {book.publisher}</p>
              <p>Description: {book.description}</p>
              <p>Price: ${book.price}</p>
              <img
                src={`http://localhost:8000/${book.image}`} // Assuming image path returned from server
                alt={book.name}
                style={{ maxWidth: "200px" }}
              />
              {book.pdf && (
                <div>
                  <p>
                    <a
                      href={`http://localhost:8000/${book.pdf}`} // Assuming PDF path returned from server
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      View PDF
                    </a>
                  </p>
                </div>
              )}
              <hr />
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default HomePage;
