function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(
    localStorage.getItem("isLoggedIn") === "true"
  );
  const [isAdmin, setIsAdmin] = useState(
    localStorage.getItem("isAdmin") === "true"
  );

  const ProtectedAdminRoute = ({ element: Element, ...rest }) => {
    return isAdmin ? (
      <Route {...rest} element={<Element />} />
    ) : (
      <Navigate to="/" />
    );
  };

  const ProtectedSignup = () => {
    return isLoggedIn ? (
      <Navigate to="/" />
    ) : (
      <Signup setIsLoggedIn={setIsLoggedIn} />
    );
  };

  return (
    <Router>
      <Nav
        isLoggedIn={isLoggedIn}
        setIsLoggedIn={setIsLoggedIn}
        isAdmin={isAdmin}
        setIsAdmin={setIsAdmin}
      />
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/explore" element={<Explore />} />
        <Route path="/dashboard" element={<Dashboard />} />

        {/* Protect AddBook and ListBook routes */}
        <ProtectedAdminRoute
          path="/addBook"
          element={AddBook}
          isAdmin={isAdmin}
        />
        <ProtectedAdminRoute
          path="/listBook"
          element={ListBook}
          isAdmin={isAdmin}
        />

        {/* Conditional rendering for protecting the /signup route */}
        <Route path="/signup" element={<ProtectedSignup />} />

        <Route
          path="/login"
          element={
            <Login isLoggedIn={isLoggedIn} setIsLoggedIn={setIsLoggedIn} />
          }
        />

        {/* Add a 404 route for unmatched paths */}
        <Route path="*" element={<b>Page Not Found</b>} />
      </Routes>
    </Router>
  );
}

export default App;
