import React, { useState, useEffect } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useNavigate, useParams } from "react-router-dom";
import "../../App.css";

const UpdateBook = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [book, setBook] = useState({
    name: "",
    author: "",
    genre: "",
    description: "",
    publisher: "",
    price: 0,
    image: "",
    pdf: "",
  });

  const [fileNames, setFileNames] = useState({
    imageName: "",
    pdfName: "",
  });

  const [previousImage, setPreviousImage] = useState("");
  const [previousPdf, setPreviousPdf] = useState("");

  useEffect(() => {
    async function fetchBook() {
      try {
        const response = await axios.get(`http://localhost:8000/books/${id}`);
        const {
          name,
          author,
          genre,
          description,
          publisher,
          price,
          image,
          pdf,
        } = response.data;

        setBook({
          name,
          author,
          genre,
          description,
          publisher,
          price,
          image: "",
          pdf: "",
        });

        setFileNames({
          imageName: image || "",
          pdfName: pdf || "",
        });

        setPreviousImage(image);
        setPreviousPdf(pdf);
      } catch (error) {
        console.error("Error fetching book:", error);
      }
    }
    fetchBook();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === "image" || name === "pdf") {
      setBook((prevBook) => ({
        ...prevBook,
        [name]: e.target.files[0] || null,
      }));
    } else {
      setBook((prevBook) => ({
        ...prevBook,
        [name]: value,
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("name", book.name);
    formData.append("author", book.author);
    formData.append("genre", book.genre);
    formData.append("description", book.description);
    formData.append("publisher", book.publisher);
    formData.append("price", book.price);
    formData.append("image", book.image || previousImage || "");
    formData.append("pdf", book.pdf || previousPdf || "");

    try {
      const response = await axios.put(
        `http://localhost:8000/books/${id}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      console.log("Book updated:", response.data);
      navigate("/");
    } catch (error) {
      console.error("Error updating book:", error);
    }
  };

  return (
    <div className="input-container">
      <ToastContainer />
      <form
        style={{
          display: "flex",
          flexDirection: "column",
        }}
        onSubmit={handleSubmit}
      >
        Name:
        <input
          type="text"
          id="name"
          name="name"
          value={book.name}
          onChange={handleChange}
        />
        Author:
        <input
          type="text"
          id="author"
          name="author"
          value={book.author}
          onChange={handleChange}
        />
        Genre:
        <input
          type="text"
          id="genre"
          name="genre"
          value={book.genre}
          onChange={handleChange}
        />
        Publisher:
        <input
          type="text"
          id="publisher"
          name="publisher"
          value={book.publisher}
          onChange={handleChange}
        />
        Description:
        <textarea
          name="description"
          rows="4"
          value={book.description}
          onChange={handleChange}
        ></textarea>
        Price:
        <input
          type="number"
          id="price"
          name="price"
          value={book.price}
          onChange={handleChange}
        />
        Image:
        <input
          type="file"
          name="image"
          accept="image/*"
          onChange={handleChange}
        />
        {fileNames.imageName && <p>{fileNames.imageName}</p>}
        PDF:
        <input type="file" name="pdf" accept=".pdf" onChange={handleChange} />
        {fileNames.pdfName && <p>{fileNames.pdfName}</p>}
        <button type="submit">Update Book</button>
      </form>
    </div>
  );
};

export default UpdateBook;
