import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import "../../App.css";

const UpdateBook = () => {
  const { id } = useParams();

  const [book, setBook] = useState({
    name: "",
    author: "",
    genre: "",
    description: "",
    publisher: "",
    price: 0,
    image: "",
    pdf: "",
  });

  const [fileNames, setFileNames] = useState({
    imageName: "",
    pdfName: "",
  });

  useEffect(() => {
    async function fetchBook() {
      try {
        const response = await axios.get(`http://localhost:8000/books/${id}`);
        const {
          name,
          author,
          genre,
          description,
          publisher,
          price,
          image,
          pdf,
        } = response.data;

        setBook({
          name,
          author,
          genre,
          description,
          publisher,
          price,
          image: "",
          pdf: "",
        });

        setFileNames({
          imageName: image || "",
          pdfName: pdf || "",
        });
      } catch (error) {
        console.error("Error fetching book:", error);
      }
    }
    fetchBook();
  }, [id]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setBook((prevBook) => ({
      ...prevBook,
      image: file,
    }));
    setFileNames((prevNames) => ({
      ...prevNames,
      imageName: file ? file.name : "",
    }));
  };

  const handlePdfChange = (e) => {
    const file = e.target.files[0];
    setBook((prevBook) => ({
      ...prevBook,
      pdf: file,
    }));
    setFileNames((prevNames) => ({
      ...prevNames,
      pdfName: file ? file.name : "",
    }));
  };

  const handleSubmit = async (e) => {
    // handle form submission here...
  };

  return (
    <div className="input-container">
      <h2>Update Book</h2>
      <form
        style={{
          display: "flex",
          flexDirection: "column",
        }}
        onSubmit={handleSubmit}
      >
        Name:
        <input type="text" name="name" value={book.name} />
        Author:
        <input type="text" name="author" value={book.author} />
        Genre:
        <input type="text" name="genre" value={book.genre} />
        Publisher:
        <input type="text" name="publisher" value={book.publisher} />
        Description:
        <textarea
          name="description"
          rows="4"
          value={book.description}
        ></textarea>
        Price:
        <input type="number" name="price" value={book.price} />
        <label htmlFor="imageUpload" style={{ cursor: "pointer" }}>
          {fileNames.imageName || "Choose Image File"}
        </label>
        <input
          type="file"
          id="imageUpload"
          accept="image/*"
          style={{ display: "none" }}
          onChange={handleImageChange}
        />
        <label htmlFor="pdfUpload" style={{ cursor: "pointer" }}>
          {fileNames.pdfName || "Choose PDF File"}
        </label>
        <input
          type="file"
          id="pdfUpload"
          accept=".pdf"
          style={{ display: "none" }}
          onChange={handlePdfChange}
        />
        <button type="submit">Update Book</button>
      </form>
    </div>
  );
};

export default UpdateBook;
