import React, { useState } from "react";
import axios from "axios";

const AddBook = () => {
  const [bookData, setBookData] = useState({
    name: "",
    author: "",
    genre: "",
    description: "",
    publisher: "",
    price: 0,
    image: null,
    pdf: null,
  });

  const handleChange = (e) => {
    if (e.target.type === "file") {
      setBookData({
        ...bookData,
        [e.target.name]: e.target.files[0], // Store file object in state
      });
    } else {
      setBookData({
        ...bookData,
        [e.target.name]: e.target.value,
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append("name", bookData.name);
      formData.append("author", bookData.author);
      formData.append("genre", bookData.genre);
      formData.append("description", bookData.description);
      formData.append("publisher", bookData.publisher);
      formData.append("price", bookData.price);
      formData.append("image", bookData.image); // Append image file to FormData
      formData.append("pdf", bookData.pdf); // Append pdf file to FormData

      const response = await axios.post(
        "http://localhost:8000/users/add",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data", // Set content type for FormData
          },
        }
      );

      console.log(response.data); // Log the created book data from the backend
      // Handle success message or further actions if needed
    } catch (error) {
      console.error("Error:", error);
      // Handle error state or display an error message to the user
    }
  };

  return (
    <div style={{ display: "flex", justifyContent: "center", padding: "20px" }}>
      <h2>Add New Book</h2>
      <form
        style={{
          display: "flex",
          flexDirection: "column",
        }}
        onSubmit={handleSubmit}
      >
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          name="name"
          value={bookData.name}
          onChange={handleChange}
        />

        <label htmlFor="author">Author:</label>
        <input
          type="text"
          id="author"
          name="author"
          value={bookData.author}
          onChange={handleChange}
        />

        <label htmlFor="genre">Genre:</label>
        <input
          type="text"
          id="genre"
          name="genre"
          value={bookData.genre}
          onChange={handleChange}
        />

        <label htmlFor="publisher">Publisher:</label>
        <input
          type="text"
          id="publisher"
          name="publisher"
          value={bookData.publisher}
          onChange={handleChange}
        />

        <label htmlFor="description">Description:</label>
        <textarea
          name="description"
          rows="10"
          value={bookData.description}
          onChange={handleChange}
        ></textarea>
        <label htmlFor="price">Price:</label>
        <input
          type="number"
          id="price"
          name="price"
          value={bookData.price}
          onChange={handleChange}
        />

        <label htmlFor="image">Image:</label>
        <input
          type="file"
          id="image"
          name="image"
          accept="image/*"
          onChange={handleChange}
        />

        <label htmlFor="pdf">PDF:</label>
        <input
          type="file"
          id="pdf"
          name="pdf"
          accept=".pdf"
          onChange={handleChange}
        />

        <button type="submit">Add Book</button>
      </form>
    </div>
  );
};

export default AddBook;
