import React, { useState } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
} from "react-router-dom";
import AddBook from "../src/components/Book/AddBook";
import HomePage from "../src/components/Book/HomePage";
import Dashboard from "../src/components/Book/Dashboard";
import Nav from "../src/components/Nav/Nav";
import { Explore } from "../src/components/Book/Explore";
import ListBook from "../src/components/Book/ListBook";
import Login from "./components/Login/Login";
import Signup from "./components/Signup/Signup";
import AdminLogin from "./components/Admin/AdminLogin";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(
    localStorage.getItem("isLoggedIn") === "true"
  );
  const [isAdmin, setIsAdmin] = useState(
    localStorage.getItem("isAdmin") === "true"
  );

  const userRoutes = (
    <>
      <Route path="/explore" element={<Explore />} />
      <Route path="/dashboard" element={<Dashboard />} />
    </>
  );

  const adminRoutes = (
    <>
      <Route path="/explore" element={<Explore />} />
      <Route path="/dashboard" element={<Dashboard />} />
      <Route path="/addBook" element={<AddBook />} />
      <Route path="/listBook" element={<ListBook />} />
    </>
  );

  return (
    <Router>
      <Nav
        isLoggedIn={isLoggedIn}
        setIsLoggedIn={setIsLoggedIn}
        isAdmin={isAdmin}
        setIsAdmin={setIsAdmin}
      />
      <Routes>
        <Route path="/" element={<HomePage />} />
        {isLoggedIn ? (
          userRoutes
        ) : (
          <Route path="*" element={<Navigate to="/" />} />
        )}

        {/* Conditional rendering for protecting the /signup route */}
        {!isLoggedIn && (
          <Route
            path="/signup"
            element={<Signup setIsLoggedIn={setIsLoggedIn} />}
          />
        )}

        {/* Conditional rendering for protecting the /login route */}
        {!isLoggedIn && (
          <Route
            path="/login"
            element={<Login setIsLoggedIn={setIsLoggedIn} />}
          />
        )}

        {/* Conditional rendering for protecting the /admin-login route */}
        {(!isLoggedIn || isAdmin) && (
          <Route
            path="/admin-login"
            element={<AdminLogin setIsAdmin={setIsAdmin} />}
          />
        )}

        {/* Redirect to home page for unauthorized access */}
        {isLoggedIn && <Route path="/signup" element={<Navigate to="/" />} />}
        {isLoggedIn && <Route path="/login" element={<Navigate to="/" />} />}
        {(!isLoggedIn || !isAdmin) && (
          <Route path="/admin-login" element={<Navigate to="/" />} />
        )}

        {/* Add a 404 route for unmatched paths */}
        <Route path="*" element={<b>Page Not Found</b>} />
      </Routes>
    </Router>
  );
}

export default App;
