import React, { useState, useEffect } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";
import "../../App.css";

const UpdateBook = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [book, setBook] = useState({
    name: "",
    author: "",
    genre: "",
    description: "",
    publisher: "",
    price: 0,
    image: "",
    pdf: "",
  });

  const [fileNames, setFileNames] = useState({
    imageName: "",
    pdfName: "",
  });

  const [previousImage, setPreviousImage] = useState("");
  const [previousPdf, setPreviousPdf] = useState("");

  useEffect(() => {
    async function fetchBook() {
      try {
        const response = await axios.get(`http://localhost:8000/books/${id}`);
        const {
          name,
          author,
          genre,
          description,
          publisher,
          price,
          image,
          pdf,
        } = response.data;

        setBook({
          name,
          author,
          genre,
          description,
          publisher,
          price,
          image: "",
          pdf: "",
        });

        setFileNames({
          imageName: image || "",
          pdfName: pdf || "",
        });

        setPreviousImage(image);
        setPreviousPdf(pdf);
      } catch (error) {
        console.error("Error fetching book:", error);
      }
    }
    fetchBook();
  }, [id]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setBook((prevBook) => ({
      ...prevBook,
      image: file,
    }));
    setFileNames((prevNames) => ({
      ...prevNames,
      imageName: file ? file.name : "",
    }));
  };

  const handlePdfChange = (e) => {
    const file = e.target.files[0];
    setBook((prevBook) => ({
      ...prevBook,
      pdf: file,
    }));
    setFileNames((prevNames) => ({
      ...prevNames,
      pdfName: file ? file.name : "",
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const updatedFormData = new FormData();
    updatedFormData.append("name", book.name);
    updatedFormData.append("author", book.author);
    updatedFormData.append("genre", book.genre);
    updatedFormData.append("description", book.description);
    updatedFormData.append("publisher", book.publisher);
    updatedFormData.append("price", book.price);
    updatedFormData.append("image", book.image);
    updatedFormData.append("pdf", book.pdf);

    try {
      const response = await axios.put(
        `http://localhost:8000/books/${id}`,
        updatedFormData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      console.log("Book updated:", response.data);
      navigate("/");
    } catch (error) {
      console.error("Error updating book:", error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setBook((prevBook) => ({
      ...prevBook,
      [name]: value,
    }));
  };

  return (
    <div className="input-container">
      <h2>Update Book</h2>
      <form
        style={{
          display: "flex",
          flexDirection: "column",
        }}
        onSubmit={handleSubmit}
      >
        Name:
        <input
          type="text"
          name="name"
          value={book.name}
          onChange={handleChange}
        />
        Author:
        <input
          type="text"
          name="author"
          value={book.author}
          onChange={handleChange}
        />
        Genre:
        <input
          type="text"
          name="genre"
          value={book.genre}
          onChange={handleChange}
        />
        Publisher:
        <input
          type="text"
          name="publisher"
          value={book.publisher}
          onChange={handleChange}
        />
        Description:
        <textarea
          name="description"
          rows="4"
          value={book.description}
          onChange={handleChange}
        ></textarea>
        Price:
        <input
          type="number"
          name="price"
          value={book.price}
          onChange={handleChange}
        />
        Image:
        <input type="file" accept="image/*" onChange={handleImageChange} />
        <label>{fileNames.imageName || "Choose Image File"}</label>
        PDF:
        <input type="file" accept=".pdf" onChange={handlePdfChange} />
        <label>{fileNames.pdfName || "Choose PDF File"}</label>
        <button type="submit">Update Book</button>
      </form>
    </div>
  );
};

export default UpdateBook;
