import React from "react";
import { useNavigate } from "react-router-dom";

const Nav = ({ isLoggedIn, setIsLoggedIn, isAdmin, setIsAdmin }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    setIsLoggedIn(false);
    setIsAdmin(false);
    localStorage.removeItem("isLoggedIn");
    localStorage.removeItem("isAdmin");
    localStorage.removeItem("firstName");
    navigate("/");
  };

  const firstName = localStorage.getItem("firstName");

  const handleUpdateBook = () => {
    // Example function to update a book (Replace with your logic)
    console.log("Update Book logic");
    // For example, navigate to the updateBook route programmatically
    navigate("/updatebook");
  };

  return (
    <nav className="navbar">
      <ul className="navbar-list">
        <li className="navbar-item">
          <a href="/" className="navbar-link">
            Home
          </a>
        </li>
        {isAdmin ? (
          <>
            <li className="navbar-item">
              <a href="/addbook" className="navbar-link">
                Add Book
              </a>
            </li>
            <li className="navbar-item">
              <a href="/listbook" className="navbar-link">
                List Book
              </a>
            </li>
            <li className="navbar-item">
              <button onClick={handleUpdateBook} style={{ display: "none" }}>
                Update Book
              </button>
            </li>
            <li className="navbar-item">
              <span className="navbar-link" onClick={handleLogout}>
                Logout
              </span>
            </li>
          </>
        ) : isLoggedIn ? (
          <>
            <li className="navbar-item">
              <span className="navbar-link" onClick={handleLogout}>
                Logout({firstName})
              </span>
            </li>
          </>
        ) : (
          <>
            <li className="navbar-item">
              <a href="/signup" className="navbar-link">
                Sign Up
              </a>
            </li>
            <li className="navbar-item">
              <a href="/login" className="navbar-link">
                Login
              </a>
            </li>
            <li className="navbar-item">
              <a href="/admin-login" className="navbar-link">
                Admin
              </a>
            </li>
          </>
        )}
      </ul>
    </nav>
  );
};

export default Nav;
