import React, { useState, useEffect } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useNavigate, useParams } from "react-router-dom";
import "../../App.css";

const UpdateBook = () => {
  const navigate = useNavigate();
  const { id } = useParams();

  const [book, setBook] = useState({
    name: "",
    author: "",
    genre: "",
    publisher: "",
    description: "",
    price: 0,
    image: "", // For the image file name
    pdf: "", // For the pdf file name
    imageFile: null, // For handling image file upload
    pdfFile: null, // For handling pdf file upload
  });

  useEffect(() => {
    async function fetchBook() {
      try {
        const response = await axios.get(`http://localhost:8000/books/${id}`);
        const {
          name,
          author,
          genre,
          publisher,
          description,
          price,
          image,
          pdf,
        } = response.data;

        setBook({
          name,
          author,
          genre,
          publisher,
          description,
          price,
          image,
          pdf,
        });
      } catch (error) {
        console.error("Error fetching book:", error);
      }
    }
    fetchBook();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const formData = new FormData();
      formData.append("name", book.name);
      formData.append("author", book.author);
      formData.append("genre", book.genre);
      formData.append("publisher", book.publisher);
      formData.append("description", book.description);
      formData.append("price", book.price);
      formData.append("image", book.imageFile || book.image); // Use existing image if no new file selected
      formData.append("pdf", book.pdfFile || book.pdf); // Use existing pdf if no new file selected

      const response = await axios.put(
        `http://localhost:8000/books/${id}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (response.status === 200) {
        toast.success("Book updated successfully");
        navigate("/"); // Redirect to the home page after updating the book
      } else {
        toast.error("Failed to update book");
      }
    } catch (err) {
      toast.error("Failed to update book. Error:", err.message);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBook({ ...book, [name]: value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setBook({
      ...book,
      imageFile: file, // Store the file object
    });
  };

  const handlePdfChange = (e) => {
    const file = e.target.files[0];
    setBook({
      ...book,
      pdfFile: file, // Store the file object
    });
  };

  return (
    <div className="input-container">
      <ToastContainer />
      <form
        style={{
          display: "flex",
          flexDirection: "column",
        }}
        onSubmit={handleSubmit}
      >
        Name:
        <input
          type="text"
          id="name"
          name="name"
          value={book.name}
          onChange={handleInputChange}
        />
        Author:
        <input
          type="text"
          id="author"
          name="author"
          value={book.author}
          onChange={handleInputChange}
        />
        Genre:
        <input
          type="text"
          id="genre"
          name="genre"
          value={book.genre}
          onChange={handleInputChange}
        />
        Publisher:
        <input
          type="text"
          id="publisher"
          name="publisher"
          value={book.publisher}
          onChange={handleInputChange}
        />
        Description:
        <textarea
          name="description"
          rows="4"
          value={book.description}
          onChange={handleInputChange}
        ></textarea>
        Price:
        <input
          type="number"
          id="price"
          name="price"
          value={book.price}
          onChange={handleInputChange}
        />
        Image:
        <input
          type="file"
          id="image"
          name="image"
          onChange={handleImageChange}
        />
        PDF:
        <input type="file" id="pdf" name="pdf" onChange={handlePdfChange} />
        <button type="submit">Update Book</button>
      </form>
    </div>
  );
};

export default UpdateBook;
