import React, { useState, useEffect } from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useNavigate, useParams } from "react-router-dom";
import "../../App.css";

const UpdateBook = () => {
  const navigate = useNavigate();
  const { id } = useParams();

  const [book, setBook] = useState({
    name: "",
    author: "",
    genre: "",
    publisher: "",
    description: "",
    price: 0,
    image: "",
    pdf: "",
    currentImage: "",
    currentPDF: "",
  });

  useEffect(() => {
    async function fetchBook() {
      try {
        const response = await axios.get(`http://localhost:8000/books/${id}`);
        setBook({
          ...response.data,
          currentImage: response.data.image,
          currentPDF: response.data.pdf,
        });
      } catch (error) {
        console.error("Error fetching book:", error);
      }
    }
    fetchBook();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("name", book.name);
    formData.append("author", book.author);
    formData.append("genre", book.genre);
    formData.append("publisher", book.publisher);
    formData.append("description", book.description);
    formData.append("price", book.price);
    formData.append("image", book.image);
    formData.append("pdf", book.pdf);

    try {
      const response = await axios.put(
        `http://localhost:8000/books/${id}`,
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      if (response.status === 200) {
        toast.success("Book updated successfully");
        navigate("/"); // Redirect to the home page after updating the book
      } else {
        toast.error("Failed to update book");
      }
    } catch (err) {
      toast.error("Failed to update book. Error:", err.message);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setBook({ ...book, [name]: value });
  };

  const handleImageChange = (e) => {
    setBook({
      ...book,
      image: e.target.files[0],
      currentImage: e.target.files[0].name,
    });
  };

  const handlePDFChange = (e) => {
    setBook({
      ...book,
      pdf: e.target.files[0],
      currentPDF: e.target.files[0].name,
    });
  };

  return (
    <div className="input-container">
      <ToastContainer />
      <form
        style={{
          display: "flex",
          flexDirection: "column",
        }}
        onSubmit={handleSubmit}
      >
        {/* Other input fields */}
        {/* ... */}
        <label htmlFor="image">
          Current Image: {book.currentImage && <span>{book.currentImage}</span>}
          <br />
          New Image:
          <input
            type="file"
            id="image"
            name="image"
            accept="image/*"
            onChange={handleImageChange}
            style={{ display: "none" }}
          />
        </label>
        <br />
        <label htmlFor="pdf">
          Current PDF: {book.currentPDF && <span>{book.currentPDF}</span>}
          <br />
          New PDF:
          <input
            type="file"
            id="pdf"
            name="pdf"
            accept=".pdf"
            onChange={handlePDFChange}
            style={{ display: "none" }}
          />
        </label>
        <br />
        <button type="submit">Update Book</button>
      </form>
    </div>
  );
};

export default UpdateBook;
