import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const HomePage = () => {
  const navigate = useNavigate();
  const [bookList, setBookList] = useState([]);
  const [tempBookList, setTempBookList] = useState([]);
  const [searchText, setSearchText] = useState("");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    async function fetchBooks() {
      try {
        setLoading(true);
        const response = await axios.get("http://localhost:8000/books");
        setBookList(response.data);
        setTempBookList(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching books:", error);
        setLoading(false);
      }
    }
    fetchBooks();
  }, []);

  useEffect(() => {
    let isMounted = true;

    async function searchBooks() {
      try {
        setLoading(true);
        const response = await axios.get(
          `localhost:8000/books/search/${searchText}`
        );
        if (response.data && isMounted) {
          setBookList(response.data);
        }
        setLoading(false);
      } catch (error) {
        console.error("Error searching books:", error);
        setLoading(false);
      }
    }

    if (searchText) {
      searchBooks();
    } else {
      setBookList(tempBookList);
    }

    return () => {
      isMounted = false; // Cleanup to prevent setting state on unmounted component
    };
  }, [searchText, tempBookList]);

  return (
    <>
      <center>
        <input
          type="text"
          placeholder="Search Books..."
          style={{ width: "55%", margin: "20px", padding: "10px" }}
          value={searchText}
          onChange={(e) => setSearchText(e.target.value)}
        />
      </center>
      {loading ? (
        <div style={{ textAlign: "center" }}>Loading...</div>
      ) : (
        <div
          style={{
            display: "flex",
            flexWrap: "wrap",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          {bookList.length > 0 ? (
            bookList.map((book, index) => {
              return (
                <div
                  onClick={() =>
                    navigate("/explore", {
                      state: {
                        book,
                      },
                    })
                  }
                  key={index}
                  style={{
                    display: "flex",
                    flexDirection: "column",
                    justifyContent: "center",
                    textAlign: "center",
                    padding: "20px",
                    boxShadow: "0px 0px 5px #ccc",
                    marginLeft: "20px",
                    marginTop: "20px",
                    cursor: "pointer",
                  }}
                >
                  <img
                    src={`http://localhost:8000/${book.image}`}
                    alt="book "
                    style={{
                      height: "250px",
                      width: "250px",
                      objectFit: "contain",
                    }}
                  />
                  {book.name}
                  <img
                    src={`http://localhost:8000/${book.image}`}
                    alt="book "
                    style={{
                      height: "250px",
                      width: "250px",
                      objectFit: "contain",
                    }}
                  />
                  {book.name}
                </div>
              );
            })
          ) : (
            <div>No Books Found</div>
          )}
        </div>
      )}
    </>
  );
};

export default HomePage;
