import React from "react";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useNavigate } from "react-router-dom";
import "./AddBook.css"; // Import your external CSS file

const AddBook = () => {
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);

    try {
      const response = await axios.post(
        "http://localhost:8000/books/add",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (response.data.id) {
        toast.success("Added New Book");
        e.target.reset(); // Reset the form after successful submission
        navigate("/"); // Redirect to the home page after adding the book
      } else {
        toast.error(response.data.message || "Failed to add book");
      }
    } catch (err) {
      toast.error(err.message || "Failed to add book");
    }
  };

  return (
    <div className="input-container">
      {" "}
      {/* Apply the class name */}
      <ToastContainer />
      <form
        style={{
          display: "flex",
          flexDirection: "column",
        }}
        onSubmit={handleSubmit}
      >
        {/* Input fields */}
        {/* Add the necessary input fields here */}
        <button type="submit">Add Book</button>
      </form>
    </div>
  );
};

export default AddBook;
